import React from "react";
import Header from "./components/Header";
import Users from "./components/Users";
import AddUser from "./components/AddUser";


class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
        usersList: [
        {
            id: 1,
            firstname: "Maksim",
            lastname: "Lebedko",
            placeOfWork: "SDvor",
            age: 13,
            isRich: false
        },
        {
            id: 2,
            firstname: "Gosha",
            lastname: "Pavlikov",
            placeOfWork: "SDvor",
            age: 21,
            isRich: true
        },
        {
            id: 3,
            firstname: "Ruslan",
            lastname: "Djambekov",
            placeOfWork: "Kontora",
            age: 41,
            isRich: true
        },
        {
            id: 4,
            firstname: "Dima",
            lastname: "Kukan",
            placeOfWork: "Kawaii",
            age: 15,
            isRich: true
        }
    ]
    }

    this.addUser = this.addUser.bind(this)
    this.deleteUser = this.deleteUser.bind(this)
}

  render() {
    return (
    <div>
      <Header title="A list of users" /> 
      <main>
        <Users usersList={this.state.usersList} onDelete={this.deleteUser}/>
      </main>
      <aside>
        <AddUser onAdd={this.addUser}/>
      </aside>
    </div>)
  }

  addUser(user) {
    const id = this.state.usersList.length + 1
    this.setState({usersList: [...this.state.usersList, {id, ...user}]})
  }

  deleteUser(userID) {
    this.setState({
      usersList: this.state.usersList.l
    })
  }
}

export default App
