import React, { useState } from "react";

const ClearAllTodos = () => {
  const [count, setCount] = useState(1);

  const increment = () => {
    setCount(prevCount =>  prevCount + 1)
    setCount(prevCount =>  prevCount + 1)
  };

  return (
    <div className="text-white text-center text-2xl">
      <button onClick={increment}>+</button>
      <span>{count}</span>
    </div>
  );
};

export default ClearAllTodos;
