import React from "react"

class Users extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            users: [
            {
                id: 1,
                firstname: "Maksim",
                lastname: "Lebedko",
                placeOfWork: "SDvor",
                age: 13,
                isRich: false
            },
            {
                id: 2,
                firstname: "Gosha",
                lastname: "Pavlikov",
                placeOfWork: "SDvor",
                age: 21,
                isRich: true
            },
            {
                id: 3,
                firstname: "Ruslan",
                lastname: "Djambekov",
                placeOfWork: "Kontora",
                age: 41,
                isRich: true
            },
            {
                id: 4,
                firstname: "Dima",
                lastname: "Kukan",
                placeOfWork: "Kawaii",
                age: 15,
                isRich: true
            }
        ]
        }
    }
    render() {
        if (this.usersList.length > 0)
            return (<div>
                {this.usersList.map((user) => (<div className="user" key={user.id}>
                    <h3>{user.firstname} {user.lastname}</h3>
                    <p>{user.placeOfWork}</p>
                    <b>{user.isRich === true ? "Rich :)" : "Poor :("}</b>
                </div>))}
            </div>)
        else
            return (<div className="user">
                <h3>A list of users is empty</h3>
            </div>)
    }
}

export default Users
