import React from "react";
import Header from "./components/Header";
import Footer from "./components/Footer";
import Image from "./components/Image";
import logo from "./img/react-logo.jpg"


class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      helpText: 0,
      userData: "Начни писать"
    }

    this.plusClick = this.plusClick.bind(this)
    this.minusClick = this.minusClick.bind(this)
  }


  componentDidUpdate(prevProp) {
    if(this.state.helpText !== "Help")
      console.log("Some")   
  }

  helpText = 0
  render() {
    return (<div className="name">
      <Header title="Header" />
    <h1>{this.state.helpText}</h1>
    <h2>{this.state.userData}</h2>
    <input onChange={event => this.setState({userData: event.target.value})}></input><br />
    
    <button onClick = {this.plusClick}>+</button>
    <button onClick = {this.minusClick}>-</button>

    <p>{this.state.helpText === "Help text!" ? "Yes" : "No"}</p>
    <Image image = {logo}/>
    <Footer />
  </div>)
  }

  plusClick() {
    this.setState({helpText: this.helpText += 1})
    console.log("Clicked!")
  }

  minusClick() {
    this.setState({helpText: this.helpText -= 1})
    console.log("minused")
  }
}

export default App
