import React from "react";

const ClearAll = (todo, removeTodo) => {
  for(let i = 0; i < todo.length; i++) {
    if (i === todo.id) {
      removeTodo(i);
    }
  }
};

const ClearAllTodos = () => {
  return (
    <div>
      <button
        onClick={() => {ClearAll(todo)}}
        className="border text-3xl text-white p-3"
      >
        Clear
      </button>
    </div>
  );
};

export default ClearAllTodos;
