import { data } from "autoprefixer";
import React, { useState } from "react";
import TodoItem from "./components/TodoItem";
import "./index.css"




const todos = [
{
  id: 1,
  title: "Finish the essay collaboration",
  isCompleted: false
},
{
  id: 2,
  title: "Read next chapter on the book",
  isCompleted: false
},
{
  id: 3,
  title: "Send the finished assignment",
  isCompleted: false
}
]



class App extends React.Component {
  render() {
    const [todos, setTodos] = useState(data)
     
    const changeTodo = (id) => {
      const copy = [...todos]
      const current = copy.find(t => t.id === id)
      current.isCompleted = !current.isCompleted
      setTodos(copy)
    }
    return (
      <div className="bg-zinc-900 h-screen py-10">
        <div className="text-white w-4/5 mx-auto">
          <h1 className="text-2xl font-bold text-center mb-10">Todo for junior</h1>
          {todos.map((todo) => <TodoItem key={todo.id} todo={todo} changeTodo={changeTodo}/>)}
        </div>
      </div>
    )
  }
}

export default App
