import React, { useState } from "react";
const word = {
  title: "jopa",
  isComp: false,
};
const ClearAllTodos = () => {
  const [done, setDone] = useState(false);
  const changeDone = () => {
    const wordCopy = done;
    wordCopy.isComp = !wordCopy.isComp;
    setDone(wordCopy);
  };

  return (
    <div className="text-white text-center text-2xl">
      <span className={`${word.isComp ? "line-through" : "no-underline"}`}>
        {word}
      </span>{" "}
      <br />
      <button onClick={changeDone}>START</button>
    </div>
  );
};

export default ClearAllTodos;
