import React from "react";

class AddUser extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            firstname: "",
            lastname: "",
            placeOfWork: "",
            age: 1,
            isRich: false
        }
    }
    
    render() {
        return (
            <form ref={(element) => this.AddUserForm = element}>
                <input placeholder="Name" onChange={(event) =>this.setState({firstname: event.target.value})}/>
                <input placeholder="Lastname" onChange={(event) =>this.setState({lastname: event.target.value})}/>
                <input placeholder="Place of work" onChange={(event) =>this.setState({placeOfWork: event.target.value})}/>
                <input placeholder="Age" onChange={(event) =>this.setState({age: event.target.value})}/>
                <label htmlFor="isRich">Are you rich?</label>
                <input type={"checkbox"} id="isRich" onChange={(event) =>this.setState({isRich: event.target.checked})}/>
                <button type="button" onClick={() => {
                    this.AddUserForm.reset()
                    this.props.onAdd({
                    firstname: this.state.firstname,
                    lastname: this.state.lastname,
                    placeOfWork: this.state.placeOfWork,
                    age: this.state.age,
                    isRich: this.state.isRich
                })}}>Add</button>
            </form>
        )
    }
}

export default AddUser
