import React from "react";
import Check from "./Check";
import cn from "classnames";
import { BsTrash } from "react-icons/bs";

const TodoItem = ({ todo, changeTodo, removeTodo }) => {
  return (
    <div className="flex items-center justify-between mb-4 rounded-2xl bg-zinc-800 p-5 w-full">
      <span
        className="flex items-center cursor-pointer select-none"
        onClick={() => changeTodo(todo.id)}
      >
        <Check isCompleted={todo.isCompleted} />
        <span
          className={cn({
            "line-through": todo.isCompleted,
          })}
        >
          {todo.title}
        </span>
        <span
          className={`${
            todo.isCompleted ? "visible text-zinc-600 ml-3" : "invisible"
          }`}
        >
          done
        </span>
      </span>

      <button onClick={() => removeTodo(todo.id)}>
        <BsTrash
          size={22}
          className="text-gray-600 hover:text-red-700 transition-colors ease-in-out duration-300"
        />
      </button>

      <button onClick={() => 
      todo.id.forEach(element => {
        removeTodo(element);
      })
      } className="border text-3xl text-white p-3">
      </button>
    </div>
  );
};

export default TodoItem;
